import React from 'react';
import useStyles from './styles';
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";import Grid from '@material-ui/core/Grid';
import Comments from '../../Comments/Comment/Comment';

const userId = 'Tom';
const date = '27/02/21';
const description = 'Lorem';

const Post = props => {
    const { userId, description, date } = props;
    const classes = useStyles();
    return (
        <>
        <Card>
            <CardHeader classes={classes.cardHeader}
                title= {userId}
                date= {date}
            />
            <CardContent>
                <Typography variant="body2" component="p">
                    {description}
                </Typography>
            </CardContent>
        </Card>
        <Grid item xs={12}>
            <Comments />
        </Grid>
        </>
    )
}

export default Post;
